import { createAsyncThunk } from "@reduxjs/toolkit";
import {createPostApi,getPostApi} from "../../api/postApi";


export const uploadPostThunk = createAsyncThunk(
    '/',
    async (postData, thunkAPI) => {
        try {
            const response = await createPostApi(postData);
            console.log(response.data);
            return response.data;
        } catch (error) {
            return thunkAPI.rejectWithValue(error.response.data);
        }
    }
);


export const uploadImageThunk = createAsyncThunk(
    '/',
    async (imageData, thunkAPI) => {
        try {
            const response = await createPostApi(imageData);
            console.log(response.data);
            return response.data;
        } catch (error) {
            return thunkAPI.rejectWithValue(error.response.data);
        }
    }   
);
