import { createSlice } from "@reduxjs/toolkit";
import { loginApi, signupApi } from "../../api/authApi";
const initialState = {
  userData: null,
  isLoading: false,
  error: null,
};

const authSlice = createSlice({
  name: "auth",
  initialState,
  reducers: {
    signup: (state, action) => {
      state.isLoading = true;
      const data = fetchSignupData(action.payload);
      state.userData = data;
      state.isLoading = false;

      // signupApi(action.payload).then((res) => {
      //   localStorage.setItem("signupToken", JSON.stringify(res.data));
      // }
      //   );
    },
    login: (state, action) => {
      state.isLoading = true;
      const data = fetchLoginData(action.payload);
      console.log(data);
      state.userData = data;
      state.isLoading = false;

      // loginApi(action.payload).then((res) => {
      //   localStorage.setItem("loginToken", JSON.stringify(res.data));
      // });
    },
    logout: (state, action) => {},
  },
});

export const { login, logout, signup } = authSlice.actions;

export default authSlice.reducer;

async function fetchLoginData(loginData) {
  try {
    const { data } = await loginApi(loginData);
    return data;
  } catch (error) {
    initialState.error = error;
    console.log(error);
  }
}


async function fetchSignupData(signupData){
  try {
    const { data } = await signupApi(signupData);
    return data;
  } catch (error) {
    initialState.error=error;
    console.log(error);
  }
};
