import React, { useState, useRef } from "react";
import ProfileImage from "../../img/profileImg.jpg";
import "./SharePost.css";
import { UilScenery } from "@iconscout/react-unicons";
import { UilLocationPoint } from "@iconscout/react-unicons";
import { UilPlayCircle } from "@iconscout/react-unicons";
import { UilTimes } from "@iconscout/react-unicons";
import { useSelector } from "react-redux";

const SharePost = () => {
  const [img, setimg] = useState(null);
  const imgRef = useRef();
  const descRef = useRef();
  const user = useSelector((state) => state.auth.userData);
  const onImgChange = (e) => {
    if (e.target.files[0] === undefined) {
      return;
    }
    setimg(e.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const postForm={
      userId: user._id,
      desc: descRef.current.value,
    }
    if(img){
      const data = new FormData();
      const fileName = Date.now() + img.name;
      data.append("name", fileName);  
      data.append("file", img);
      postForm.img = fileName;
    }
    console.log(postForm);



  };
  return (
    <div className="SharePost">
      <img src={ProfileImage} alt="img" />
      <div>
        <input ref={descRef} required type="text" placeholder="What is in your mind?" />

        <div className="Post-opt">
          <div
            className="opt"
            style={{ color: "var(--photo)" }}
            onClick={() => imgRef.current.click()}
          >
            <UilScenery />
            Photo
          </div>
          <div className="opt" style={{ color: "var(--video)" }}>
            <UilPlayCircle />
            Video
          </div>
          <div className="opt" style={{ color: "var(--location)" }}>
            <UilLocationPoint />
            Location
          </div>

          <button onClick={handleSubmit} className="sharebtn button">Share</button>
          <div style={{ display: "none" }}>
            <input
              type="file"
              name="myImage"
              ref={imgRef}
              onChange={onImgChange}
            />
          </div>
        </div>

        {img && (
          <div className="imgPreview">
            <UilTimes
              onClick={() => setimg(null)}
              style={{ cursor: "Pointer" }}
            />
            <img src={URL.createObjectURL(img.image)} alt="img" />
          </div>
        )}
      </div>
    </div>
  );
};

export default SharePost;
